Get-ChildItem -Path "C:\Chemin\Vers\Dossier\Source" -Recurse -File | Where-Object { $_.Extension -in ".docx", ".pdf", ".txt" } | ForEach-Object { Copy-Item -Path $_.FullName -Destination "C:\Backup\$($_.Name)" -Force; If (Test-Path "C:\Backup\$($_.Name)") { Write-Host "Le fichier '$($_.Name)' a été copié avec succès." } Else { Write-Host "Échec de la copie du fichier '$($_.Name)'." } }
